import React from "react";

function Todo({ todo, toggleTodo }) {
  function handleTodoClick() {
    toggleTodo(todo.id);
  }

  const handleCkeckbox = e => {
    if (e.keyCode === 13){
      handleTodoClick()
    }
  }

  return (
    <div className="todo">
      <label>
        <input
          type="checkbox"
          checked={todo.complete}
          onChange={handleTodoClick}
          onKeyDown={handleCkeckbox}
        />
        {todo.name}
      </label>
    </div>
  );
}

export default Todo;
